void imageExchange(float** imgdata,float pic[][len],int length){
	for (int i = 0; i < length; i++){
		for (int j = 0; j < length; j++){
			imgdata[i][j] = pic[i][j];
			printf("%.1lf ", imgdata[i][j]);
		}
		printf("\n");
	}
}
int main()
{
	system("mode con:cols=400 lines=400");
	nSize inputSize={len,len};
	int outSize=10;
	int answer;
	
	float **imgdata;
	imgdata = (float **)malloc(sizeof(float *)* len);
	for (int i = 0; i < len; i++){
		imgdata[i] = (float *)malloc(sizeof(float)* len);
	}
	imageExchange(imgdata, pic, len);
	// CNN结构的初始化
	CNN* cnn=(CNN*)malloc(sizeof(CNN));
	cnnsetup(cnn,inputSize,outSize);
	/*导入训练好的数据*/
	importcnn(cnn);
	/*初始清空*/
	cnnclear(cnn);
	/*获取数字*/
	answer = cnntest(cnn, imgdata);
	printf("the answer:%d\n",answer);
	printf("test finished!!\n");
	/*释放内存*/
	for (int i = 0; i < len; i++)
		free(imgdata[i]);
	free(imgdata);
	getchar();
	return 0;
}